export const FileInputControl = ({
  name,
  label,
  placeholder,
  helperText,
  maxSize,
  extensions,
  disabled,
  fileType,
  required,
  ...otherProps
}) => {
  const {
    meta: { error, touched },
  } = useField(name);
  const { change } = useForm();
  const handleFileChange = (newFile) => {
    change(name, newFile);
  };
  return (
    <Field name={name}>
      {props => (
        <div>
          {label && (
            <div className='d-flex align-items-center'>
              <InputLabel label={label} required={required} />
              {props.input.value ? (
                <div className='d-flex m-b-10 m-l-10' style={{ cursor: 'pointer' }}>
                  <svg
                    xmlns='http://www.w3.org/2000/svg'
                    fill='none'
                    viewBox='0 0 24 24'
                    stroke='currentColor'
                    style={{ height: 20, width: 20, color: 'green' }}
                  >
                    <path
                      strokeLinecap='round'
                      strokeLinejoin='round'
                      strokeWidth={2}
                      d='M5 13l4 4L19 7'
                    />
                  </svg>
                  {props.input.value.name ? (
                    <a
                      download={props.input.value.name}
                      className='font-bold font-14 text-primary'
                      href={props.input.value}
                    >
                      View
                    </a>
                  ) : (
                    <a
                      target='_blank'
                      href={getS3Url(props.input.value)}
                      rel='noreferrer'
                      className='font-bold font-14 text-primary'
                    >
                      View
                    </a>
                  )}
                </div>
              ) : null}
            </div>
          )}
          {helperText && (
            <InputHelperText
              helperText={helperText}
              required={(!label || !label.trim().length) && required}
            />
          )}
          <div style={{ position: 'relative' }}>
            <div>
              {fileType === 'video' &&
                ((props.input.value.name && (
                  <ReactPlayer
                    autoPlay
                    url={window.URL.createObjectURL(props.input.value)}
                    style={{ marginBottom: 10 }}
                    width={'100%'}
                    controls
                    height={'100%'}
                  />
                )) || (
                  <ReactPlayer
                    autoPlay
                    url={getS3Url(props.input.value)}
                    style={{ marginBottom: 10 }}
                    width={'100%'}
                    controls
                    height={'100%'}
                  />
                ))}
              {!disabled && (
<React.Fragment>
                <FilePicker
                  onChange={handleFileChange}
                  onError={errMsg => alert(errMsg)}
                  playing
                  maxSize={maxSize}
                  extensions={extensions}
                >
                  <button
                    class={`btn waves-effect waves-light ${
                      props.input.value ? 'btn-info' : 'btn-outline-info'
                    }  `}
                    type='button'
                    style={{ width: 150 }}
                  >
                    {props.input.value ? 'Update' : 'Choose'}{' '}
                    {(fileType === 'video' && 'video') || 'file'}
                  </button>
                </FilePicker>
            {error && touched && (
              <div className="mt-3">
                <Error name={name} />
              </div>
            )}
</React.Fragment>
              )}
              {/* {props.input.value ? (
                <div className="font-10 m-t-10 badge badge-secondary">
                  {props.input.value.name || props.input.value}
                </div>
              ) : (
                <div />
              )} */}
            </div>
          </div>
        </div>
      )}
    </Field>
  );
};

