import styled from 'styled-components';

const ContentHeader = styled.div`
  box-shadow: 0px 15px 10px -15px #111;
  padding: 0.5em;
  display: grid;
  grid-auto-flow: column;
  align-items: center;
  grid-template-columns: auto 1fr;
`;
export default ContentHeader;
